	function seq_txt_activate(){
		document.getElementById('seq').disabled=false;
		document.getElementById('seq').readOnly =false;
		document.getElementById('seq_file').disabled=true;
	}
		function seq_file_activate(){
		document.getElementById('seq').disabled=true;
		document.getElementById('seq').readOnly =true;
		document.getElementById('seq_file').disabled=false;
